import { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import Avatar from "../components/Avatar";
import Loader from "../components/Loader";
import "../style/AlbumDetail.css";

export default function AlbumDetail() {
  const { id } = useParams();
  const [album, setAlbum] = useState(null);
  const [photos, setPhotos] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchAlbumDetail() {
      setLoading(true);
      try {
        const albumRes = await fetch(
          `https://jsonplaceholder.typicode.com/albums/${id}`
        );
        const albumData = await albumRes.json();
        setAlbum(albumData);

        const [userRes, photosRes] = await Promise.all([
          fetch(
            `https://jsonplaceholder.typicode.com/users/${albumData.userId}`
          ),
          fetch(`https://jsonplaceholder.typicode.com/photos?albumId=${id}`),
        ]);

        const userData = await userRes.json();
        const photoData = await photosRes.json();

        // Ki·ªÉm tra d·ªØ li·ªáu h·ª£p l·ªá
        if (typeof albumData.title !== "string") {
          console.error("Album title is not a string:", albumData.title);
        }
        if (
          typeof userData.name !== "string" ||
          typeof userData.email !== "string"
        ) {
          console.error("User data is not valid:", userData);
        }
        if (!Array.isArray(photoData)) {
          console.error("Photo data is not an array:", photoData);
        }

        setUser(userData);
        setPhotos(photoData);
      } catch (error) {
        console.error("Error fetching data:", error);
      } finally {
        setLoading(false);
      }
    }

    fetchAlbumDetail();
  }, [id]);

  if (loading) return <Loader />;
  if (!album || !user) return <p> Kh√¥ng t√¨m th·∫•y th√¥ng tin. </p>;

  return (
    <div>
      <h1> Chi ti·∫øt Album </h1>
      {/* User Info */}{" "}
      <div className="user-info">
        <Avatar name={user.name} size={50} />{" "}
        <div className="user-details">
          <Link to={`/users/${user.id}`} className="user-name">
            {" "}
            {user.name}{" "}
          </Link>{" "}
          <p>
            {" "}
            üìß <a href={`mailto:${user.email}`}> {user.email} </a>
          </p>
        </div>{" "}
      </div>
      {/* Album Title */} <h2> {album.title} </h2>
      {/* Photo List */}{" "}
      <div className="photo-grid">
        {" "}
        {photos.map((photo) => (
          <a
            key={photo.id}
            href={photo.url}
            target="_blank"
            rel="noopener noreferrer"
          >
            <img
              src={photo.thumbnailUrl}
              alt={photo.title}
              className="photo-thumbnail"
            />
          </a>
        ))}{" "}
      </div>{" "}
    </div>
  );
}
