import { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import Avatar from "../components/Avatar";
import Loader from "../components/Loader";

export default function AlbumDetail() {
  const { id } = useParams();
  const [album, setAlbum] = useState(null);
  const [photos, setPhotos] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    console.log(id); // Log ra id Ä‘á»ƒ kiá»ƒm tra
    async function fetchAlbumDetail() {
      setLoading(true);
      const albumRes = await fetch(
        `https://jsonplaceholder.typicode.com/albums/${id}`
      );
      const albumData = await albumRes.json();
      setAlbum(albumData);

      const [userRes, photosRes] = await Promise.all([
        fetch(`https://jsonplaceholder.typicode.com/users/${albumData.userId}`),
        fetch(`https://jsonplaceholder.typicode.com/photos?albumId=${id}`),
      ]);

      const userData = await userRes.json();
      const photoData = await photosRes.json();

      // Kiá»ƒm tra vÃ  log dá»¯ liá»‡u Ä‘á»ƒ xÃ¡c nháº­n
      console.log("Album Data:", albumData);
      console.log("User Data:", userData);
      console.log("Photo Data:", photoData);

      // Kiá»ƒm tra dá»¯ liá»‡u nháº­n Ä‘Æ°á»£c cÃ³ há»£p lá»‡ khÃ´ng
      if (typeof albumData.title !== "string") {
        console.error("Album title is not a string:", albumData.title);
      }
      if (
        typeof userData.name !== "string" ||
        typeof userData.email !== "string"
      ) {
        console.error("User data is not valid:", userData);
      }
      if (!Array.isArray(photoData)) {
        console.error("Photo data is not an array:", photoData);
      }

      setUser(userData);
      setPhotos(photoData);
      setLoading(false);
    }

    fetchAlbumDetail();
  }, [id]);

  if (loading) return <Loader />;
  if (!album || !user) return <p> KhÃ´ng tÃ¬m tháº¥y thÃ´ng tin. </p>;

  return (
    <div>
      <h1> Chi tiáº¿t Album </h1> {/ * User Info * /}{" "}
      <div
        style={{ display: "flex", alignItems: "center", marginBottom: "20px" }}
      >
        <Avatar name={user.name} size={50} />{" "}
        <div style={{ marginLeft: "10px" }}>
          <Link
            to={`/users/${user.id}`}
            style={{ fontSize: "18px", fontWeight: "bold", color: "blue" }}
          >
            {user.name}{" "}
          </Link>{" "}
          <p>
            {" "}
            ðŸ“§ <a href={`mailto:${user.email}`}> {user.email} </a>{" "}
          </p>{" "}
        </div>{" "}
      </div>{" "}
      {/* Album Title */} <h2> {album.title} </h2> {/ * Photo List * /}{" "}
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(120px, 1fr))",
          gap: "10px",
        }}
      >
        {photos.map((photo) => (
          <a
            key={photo.id}
            href={photo.url}
            target="_blank"
            rel="noopener noreferrer"
          >
            <img
              src="https://picsum.photos/150"
              alt="random placeholder"
              style={{ width: "100%", borderRadius: "8px", cursor: "pointer" }}
            />{" "}
          </a>
        ))}{" "}
      </div>{" "}
    </div>
  );
}
