import { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import Avatar from "../components/Avatar";
import Loader from "../components/Loader";

export default function AlbumDetail() {
  const { id } = useParams();
  console.log(id); // Ki·ªÉm tra gi√° tr·ªã c·ªßa `id` l√† g√¨
  const [album, setAlbum] = useState(null);
  const [photos, setPhotos] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchAlbumDetail() {
      setLoading(true);
      const albumRes = await fetch(
        `https://jsonplaceholder.typicode.com/albums/${id}`
      );
      const albumData = await albumRes.json();
      console.log(albumData); // Log d·ªØ li·ªáu album tr·∫£ v·ªÅ ƒë·ªÉ ki·ªÉm tra
      setAlbum(albumData);

      const [userRes, photosRes] = await Promise.all([
        fetch(`https://jsonplaceholder.typicode.com/users/${albumData.userId}`),
        fetch(`https://jsonplaceholder.typicode.com/photos?albumId=${id}`),
      ]);

      const userData = await userRes.json();
      const photoData = await photosRes.json();
      console.log(userData); // Log d·ªØ li·ªáu ng∆∞·ªùi d√πng
      console.log(photoData); // Log d·ªØ li·ªáu h√¨nh ·∫£nh
      setUser(userData);
      setPhotos(photoData);
      setLoading(false);
    }

    fetchAlbumDetail();
  }, [id]);

  if (loading) return <Loader />;
  if (!album || !user || photos.length === 0)
    return <p> Kh√¥ng t√¨m th·∫•y th√¥ng tin. </p>;

  return (
    <div>
      <h1> Chi ti·∫øt Album </h1> {/ * User Info * /}{" "}
      <div
        style={{ display: "flex", alignItems: "center", marginBottom: "20px" }}
      >
        <Avatar name={user.name} size={50} />{" "}
        <div style={{ marginLeft: "10px" }}>
          <Link
            to={`/users/${user.id}`}
            style={{ fontSize: "18px", fontWeight: "bold", color: "blue" }}
          >
            {" "}
            {user.name}{" "}
          </Link>{" "}
          <p>
            {" "}
            üìß <a href={`mailto:${user.email}`}> {user.email} </a>{" "}
          </p>{" "}
        </div>{" "}
      </div>{" "}
      {/* Album Title */} <h2> {album.title} </h2> {/ * Photo List * /}{" "}
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(120px, 1fr))",
          gap: "10px",
        }}
      >
        {" "}
        {photos.map((photo) => (
          <a
            key={photo.id}
            href={photo.url}
            target="_blank"
            rel="noopener noreferrer"
          >
            <img
              src={photo.thumbnailUrl}
              alt={photo.title}
              style={{ width: "100%", borderRadius: "8px", cursor: "pointer" }}
            />{" "}
          </a>
        ))}{" "}
      </div>{" "}
    </div>
  );
}
